// middleware.ts
import { apiShieldMiddleware } from '@apishield/next';

export default apiShieldMiddleware({
  publicKey: 'YOUR_PUBLIC_KEY',
});

# /etc/nginx/conf.d/apishield-rate-limit.conf
limit_req_zone $binary_remote_addr zone=login_zone:10m rate=10r/s;

server {
  location /login {
    limit_req zone=login_zone burst=20 nodelay;
    proxy_pass http://app_upstream;
  }
}
